	$("#fav_cat_id_"+idval).html(result);		}	});}function chkPractitionerDisplayOrNot(){  var practitioner_id=$('#practitioner_id').val();  if(practitioner_id!='')  {  $('#practionersdisplay').hide();  }   else  {   $('#practionersdisplay').show();  }}function chkUserDisplayOrNot(){  var user_id=$('#user_id').val();  if(user_id!='')  {  $('#userdisplay').hide();  }   else  {   $('#userdisplay').show();  }}function getModuleWiseCriteriaScaleValues(numvalue){        	  var report_module = $('#module_id_'+numvalue).val();           var module_criteria = $('#module_criteria_'+numvalue).val();                    var pro_user_id = '';                   var criteria_scale_range = $('#criteria_scale_range_'+numvalue).val();                  var num = numvalue;               //var sub_cat = $("#fav_cat_id_"+idval).val();        //alert(parent_cat_id);	var dataString = 'action=getmodulewisecriteriascalevaluesVivek&report_module='+report_module+'&module_criteria='+module_criteria+'&criteria_scale_range='+criteria_scale_range+'&pro_user_id='+pro_user_id+'&num='+num;	$.ajax({		type: "POST",		url: "include/remote.php",		data: dataString,		cache: false,		success: function(result)		{			                        			$("#idcriteriascalevalues"+numvalue).html(result);		}	});}function toggleCriteriaScaleShow(num){      var module_criteria = $('#module_criteria_'+num).val();    if (module_criteria == "")     {        $('#idcriteriascaleshow').hide();    }    else     { 	        $('#idcriteriascaleshow').show();    }}function getModuleWiseCriteriaOptionsVivek(numvalue){        	 var report_module = $('#module_id_'+numvalue).val();         var num = numvalue;               //var sub_cat = $("#fav_cat_id_"+idval).val();        //alert(parent_cat_id);	var dataString = 'action=getmodulewisecriteriaoptionsVivek&report_module='+report_module+'&num='+num;	$.ajax({		type: "POST",		url: "include/remote.