				}				$output .= $str. ' ';			}		}		else		{			$temp_len = strlen($string);			if($temp_len > 15)			{				$str = substr($string, 0, 15) . ' ' ;				$temp_str2 =  substr($string, 15);				if( strlen($temp_str2)> 15)				{					$temp_str2 = $this->get_clean_br_string($temp_str2);				}				$str .= $temp_str2;				}			else			{				$str = $string;			}			$output .= $str. ' ';		}			}			return $output;}public function getBannerString($banner){	$search = 'v=';	$pos = strpos($banner, $search);	$str = strlen($banner);	$rest = substr($banner, $pos+2, $str);	//echo $rest;	return 'http://www.youtube.com/embed/'.$rest;}public function reSendOTP($tdata)    {        $my_DBH = new DatabaseHandler();        $DBH = $my_DBH->raw_handle();        $DBH->beginTransaction();        $return = false;				try {			$sql = "UPDATE `tblusers` SET 					`user_otp` = :user_otp					WHERE `mobile` = :mobile_no  ";			$STH = $DBH->prepare($sql);			$STH->execute(array(				':user_otp' => addslashes($tdata['user_otp']),				':mobile_no' => addslashes($tdata['mobile_no'])			));			$DBH->commit();						$return = true;		} catch (Exception $e) {						return false;		}			        return $return;    }       public function getOTPSmsText($tdata)    {        $return = $tdata['user_otp']." is your otp";        return $return;    }public function sendSMS($tdata) {        $user= SMS_USERNAME;        $password = SMS_PASSWORD;        $msisdn = $tdata['mobile_no'];        // $otp = $tdata['otp'];        $sid = 'AIMSEL';        $msg = urlencode($tdata['sms_message']);        $url = "http://smpp.keepintouch.co.in/vendorsms/pushsms.aspx";        $parameters = "user=$user&password=$password&msisdn=$msisdn&sid=$sid&msg=$msg&fl=0&gwid=2";        $ch = curl_init($url);        curl_setopt($ch, CURLOPT_POST,1);        