			{				$str = 'AM';			}			else			{				$str = 'PM';			} 								$val = sprintf('%02d:%02d', $hour, $minute);						$val = $val.' '.$str;			if($time == $val)			{				$selected = ' selected ';			}			else			{				$selected = '';			}			$option_str .= '<option value="'.$val.'" '.$selected.' >'.$val.'</option>';		} 	}		return $option_str;}public function getFavCategoryRamakant($fav_cat_type_id,$fav_cat_id)	{            $DBH = new DatabaseHandler();            $option_str = '';		                        $fav_cat_type_id = explode(',', $fav_cat_type_id);            $fav_cat_type_id = implode('\',\'', $fav_cat_type_id);                       $sql = "SELECT * FROM `tblcustomfavcategory` "                    . "LEFT JOIN tblfavcategory ON tblcustomfavcategory.favcat_id = tblfavcategory.fav_cat_id WHERE tblcustomfavcategory.fav_cat_type_id IN('".$fav_cat_type_id."') and tblfavcategory.fav_cat_status = 1 and tblfavcategory.fav_cat_deleted = 0 and tblcustomfavcategory.cat_deleted=0 and tblcustomfavcategory.cat_status = 1 ORDER BY tblfavcategory.fav_cat ASC";            //echo $sql;            $STH = $DBH->query($sql);            if( $STH->rowCount() > 0 )            {                while($row = $STH->fetch(PDO::FETCH_ASSOC))                {                    if($row['favcat_id'] == $fav_cat_id)                    {                            $sel = ' selected ';                    }                    else                    {                            $sel = '';                    }	                    $cat_name = $row['fav_cat'];                    $option_str .= '<option value="'.$row['favcat_id'].'" '.$sel.'>'.stripslashes($cat_name).'</option>';                }            }            //echo $option_str;                        return $option_str;	}         public function getScrollingBarCode($page_id){