iDiffYear--;    }            return $iDiffYear;}  public function getHeightOfUser($user_id){	$DBH = new DatabaseHandler();	$height = 0;	$height_id = $this->getHeightIdOfUser($user_id);			$sql = "SELECT * FROM `tblheights` WHERE `height_id` = '".$height_id."' ";	$STH = $DBH->query($sql);        if($STH->rowCount() > 0)        {		$row = $STH->fetch(PDO::FETCH_ASSOC);		$height = stripslashes($row['height_cms']);	}	return $height;}public function getHeightIdOfUser($user_id){	$DBH = new DatabaseHandler();	$height = 0;	$sql = "SELECT * FROM `tblusers` WHERE `user_id` = '".$user_id."' ";	$STH = $DBH->query($sql);        if($STH->rowCount() > 0)        {		$row = $row = $STH->fetch(PDO::FETCH_ASSOC);		$height = stripslashes($row['height']);	}	return $height;}// public function getWeightOfUser($user_id)// {// 	$DBH = new DatabaseHandler();// 	$weight = 0;// 	$sql = "SELECT * FROM `tblusers` WHERE `user_id` = '".$user_id."' ";// 	$STH = $DBH->query($sql);//         if($STH->rowCount() > 0)//         {// 		$row = $STH->fetch(PDO::FETCH_ASSOC);// 		$weight = stripslashes($row['weight']);// 	}// 	return $weight;// }public function Get_Digital_Life_drop($page_id){	// echo "hi";	// exit;    $my_DBH = new DatabaseHandler();    $DBH = $my_DBH->raw_handle();    $DBH->beginTransaction();    $option_str = '';    $sql = "SELECT * FROM `tblpagedropdowns` WHERE `pdm_id` = '18' AND `pd_deleted` = '0' ";    $STH = $DBH->prepare($sql);    $STH->execute();    if($STH->rowCount() > 0)    {        $row = $STH->fetch(PDO::FETCH_ASSOC);        $page_id_str = stripslashes($row['page_id_str']);        $sql2 = "SELECT * FROM `tblpages` WHERE `page_id` IN (".$page_id_str.") AND `show_in_list` = '1' AND `adviser_panel` = '0' AND `vender_panel` = '0' ORDER BY `menu_title` ASC";            //$sql = "SELECT * FRO