		$org_start_time = $start_time;		$tmp_str = explode(" ",$start_time);		$temp_time = explode(":",$tmp_str[0]);		$start_time = $temp_time[0];				$start = $start_time *60 + 0;		$end = $end_time * 60+0;				for($i = $start; $i<$end; $i += 5)		{						$minute = $i % 60;			$hour = ($i - $minute)/60;									if( ($hour >=24) && ($hour <= 36) )			{				$hour = $hour - 24;			}						if( ($hour >= 0) && ($hour < 12)  )			{				$str = 'AM';			}			else			{				$str = 'PM';			} 								$val = sprintf('%02d:%02d', $hour, $minute);						$val = $val.' '.$str;			if($this->chkIsGreaterTimeValue($val,$org_start_time))			{				if($time == $val)				{					$selected = ' selected ';				}				else				{					$selected = '';				}				$option_str .= '<option value="'.$val.'" '.$selected.' >'.$val.'</option>';			}			} 	}		return $option_str;}function getActivityTimeList($actvity_start_time,$actvity_end_time,$time_interval,$mins_arr,$skip_time_arr){	$arr_activity_time = array();		//echo"<br>Testkk: actvity_start_time = ".$actvity_start_time;	//echo"<br>Testkk: actvity_end_time = ".$actvity_end_time;	//echo"<br>Testkk: time_interval = ".$time_interval;		//echo"<br>Testkk: mins_arr - <br>";	//debug_array($mins_arr);		//echo"<br><br>";		//echo"<br>Testkk: skip_time_arr - <br>";	//debug_array($skip_time_arr);		//echo"<br><br>";		//echo"<br>Testkk: skip_time_arr[0] = ".$skip_time_arr[0];	if($skip_time_arr[0] == '')	{		$tmp_start_time = explode(":",$actvity_start_time);		$start_time = $tmp_start_time[0];			$tmp_start_min = explode(" ",$tmp_start_time[1]);		$start_min = $tmp_start_min[0];			$tmp_end_time = explode(":",$actvity_end_time);		$end_time = $tmp_end_time[0];	}	else	{		if($actvity_start_time == $skip_time_arr[0])		{			$tmp_start_time = explode(":",$actvity_start_time);			$start_time = $tmp_start_time[0];					$tmp_start_min = explode(" ",$tmp_start_time[1]);			$start_min = $tmp_start_min[0];					$tmp_end_time = explode(":",$actvity_end_time);			$end_time = $tmp_end_time[0];		}		elseif($this->chkIsGreaterTimeValue($actvity_start_time,$skip_time_arr[0]))		{			$tmp_start_time = explode(":",$actvity_start_time);			$start_time = $tmp_start_time[0];					$tmp_start_min = explode(" ",$tmp_start_time[1]);			$start_min = $tmp_start_min[0];					$tmp_end_time = explode(":",$actvity_end_time);			$end_time = $tmp_end_time[0];		}		else		{			$tmp_start_time = explode(":",$skip_time_arr[0]);			$start_time = $tmp_start_time[0];					$tmp_start_min = explode(" ",$tmp_start_time[1]);			$start_min = $tmp_start_min[0];					$tmp_end_time = explode(":",$actvity_end_time);			$end_time = $tmp_end_time[0];		}		}			//echo"<br>Testkk: start_time = ".$start_time;	//echo"<br>Testkk: start_min = ".$start_min;	//echo"<br>Testkk: end_time = ".$end_time;		$start = $start_time *60 + 0 + $start_min;	$end = $end_time * 60+0;		//echo"<br>Testkk: start = ".$start;	//echo"<br>Testkk: end = ".$end;		$tmp_next_start_time = $start;		$arr_new_activity_time = array();			for($i=0;$i<count($mins_arr);$i++)	{		if($i == '0')		{			if($skip_time_arr[$i] == '')			{						}			else			{				$tmp_next_start_time = $this->convertTimeFormatToMinutes($skip_time_arr[$i]);				}						$val = $this->convertMinutesToTimeFormat($tmp_next_start_time);			array_push($arr_new_activity_time,$val);		}		else		{			if( ($mins_arr[$i-1] == '0') || ($mins_arr[$i-1] == '') )			{				break;			}			else			{					if($skip_time_arr[$i] == '')				{					$tmp_next_start_time = $tmp_next_start_time + $mins_arr[$i-1];				}				else				{					//$tmp_next_start_time = convertTimeFormatToMinutes($skip_time_arr[$i]) + $mins_arr[$i-1];						$tmp_next_start_time = $this->convertTimeFormatToMinutes($skip_time_arr[$i]);					}								$val = $this->convertMinutesToTimeFormat($tmp_next_start_time);				array_push($arr_new_activity_time,$val);			}			}	}		if( ($mins_arr[$i-1] == '0') || ($mins_arr[$i-1] == '') )	{			}	else	{			$tmp_next_start_time = $tmp_next_start_time + $mins_arr[$i-1];		$val = $this->convertMinutesToTimeFormat($tmp_next_start_time);		array_push($arr_new_activity_time,$val);	}				//echo"<br>Testkk: arr_new_activity_time - <br>";	//debug_array($arr_new_activity_time);		//echo"<br><br>";		return $arr_new_activity_time;}function chkIsGreaterTimeValue($first_time,$second_time){	$return = true;	//echo"<br>Testkk first_time = ".$first_time;	//echo"<br>Testkk second_time = ".$second_time;	if( ($first_time != '') && ($second_time != '') )	{		$tmp_first_time_str = explode(" ",$first_time);		$tmp_first_time = explode(":",$tmp_first_time_str[0]);		$first_time_hr = $tmp_first_time[0];		$first_time_min = $tmp_first_time[1];				$tmp_second_time_str = explode(" ",$second_time);		$tmp_second_time = explode(":",$tmp_second_time_str[0]);		$second_time_hr = $tmp_second_time[0];		$second_time_min = $tmp_second_time[1];				if($first_time_hr == $second_time_hr)		{			if($first_time_min >= $second_time_min)			{				$return = true;			}			else			{				$return = false;			}		}		elseif($first_time_hr > $second_time_hr)		{			$return = true;		}		else		{			$return = false;		}	}	return $return;}function convertTimeFormatToMinutes($str_time){	$tmp_time = explode(":",$str_time);	$hr_time = $tmp_time[0];		$tmp_min = explode(" ",$tmp_time[1]);	$min_time = $tmp_min[0];		$val = $hr_time * 60 + 0 + $min_time;		return $val;}function convertMinutesToTimeFormat($tmp_next_start_time){	$minute = $tmp_next_start_time % 60;	$hour = ($tmp_next_start_time - $minute)/60;			if( ($hour >=24) && ($hour <= 36) )	{		$hour = $hour - 24;	}			if( ($hour >= 0) && ($hour < 12)  )	{		$str = 'AM';	}	else	{		$str = 'PM';	} 				$val = sprintf('%02d:%02d', $hour, $minute);		$val = $val.' '.$str;	return $val;}public function getSideCartBox()	{		$DBH = new DatabaseHandler();		$output = '';				$output .= '<div class="side-cart-header">						<h4>Your Cart</h4>						<div class="back cart-box-toggle" id="cart-box-toggle" onclick="closeCartPopup()">close</div>					</div>';			$output .= '<div class="side-cart-body">						<div class="cart">';				$cart_session_id = session_id();				if($this->isLoggedIn())		{			$user_id = $_SESSION['user_id'];		}		else		{			$user_id = 0;		}				try {			if(isset($_SESSION['current_showing_date']) && $_SESSION['current_showing_date'] != '')			{				$current_showing_date = $_SESSION['current_showing_date'];			}			else			{				$current_showing_date = '';			}						if(isset($_SESSION['topcityid']) && $_SESSION['topcityid'] != '')			{				$topcityid = $_SESSION['topcityid'];			}			else			{				$topcityid = '0';			}						if(isset($_SESSION['topareaid']) && $_SESSION['topareaid'] != '')			{				$topareaid = $_SESSION['topareaid'];			}			else			{				$topareaid = '0';			}						/*				$sql = "SELECT * FROM `tblcart` WHERE 					`cart_session_id` = '".$cart_session_id."' AND 					`cart_delivery_date` = '".$current_showing_date."' AND 					`cart_city_id` = '".$topcityid."' AND 					`c