                   }                    else                    {                        get_activity_form();                    }                                                               },                  error: function() {                    alert('There was some error performing the AJAX call!');                  }              });         }        //added by ample 04-09-20         function get_activity_form()         {            var total_row= 0;            $.ajax({                  type: "POST",                  url: "remote.php?action=getFoodSectionForm",                  // dataType: "json",                  data: {total_row:total_row},                  success : function(data){                                              $('#activity_diary').html(data);                        $("#totalRow").val(total_row);                                       },                  error: function() {                    alert('There was some error performing the AJAX call!');                  }              });         }        //add by ample         function add_more_activity()         {            var total_row=parseInt($("#totalRow").val());            var total_row= total_row+1;            $.ajax({                  type: "POST",                  url: "remote.php?action=getFoodSectionForm",                  // dataType: "json",                  data: {total_row:total_row},                  success : function(data){                                              $('#activity_diary').append(data);                        $("#totalRow").val(total_row);                                       },                  error: function() {                    alert('There was some error performing the AJAX call!');                  }              });         }         //add by ample         function remove_activity(row) {            $("#row"+row).remove();               var count_row = parseInt($("#totalRow").val());                  count_row = count_row - 1;                  $("#totalRow").val(count_row);         }         function meals_datalist(id)         {            var value = $("#meals_"+id).val();            var final_value=$('#capitals'+id+' [value="' + value + '"]').data('value');            if(final_value=='' || value=='' || value==undefined || final_value==undefined)            {               alert('Please choose items in list!');               $("#meals_"+id).val('');               return false;            }            $('#meal_id_'+id).val(final_value);            get_food_meal_info(final_value,id);            get_food_compositionInfo(final_value,id);         }        function erase_input(id)        {            $("#meals_"+id).val('');            $('#meal_measure_'+id).val('');        }        function getDailyFoodItems(data,eid="")        {            var food_type_id=data.value;           $.ajax({                  type: "POST",                  url: "remote.php?action=getDailyFoodItems",                  // dataType: "json",                  data: {food_type_id:food_type_id},                  success : function(response){                        $('#capitals'+eid).html(response);                  },                  error: function() {                    alert('There was some error performing the AJAX call!');                  }              });        }        function get_food_meal_info(meal_id,eid)        {           $.ajax({                  type: "POST",                  url: "remote.php?action=get_food_meal_info",                  dataType: "json",                  data: {meal_id:meal_id},                  success : function(res){                                             if(res.status==true)                       {                          $('#meal_measure_'+eid).val(res.data.meal_measure);                          if(res.data.weight!=='')                          {                            $('#meal_measure_'+eid).after('Weight:'+res.data.weight);                          }                       }                                       },                  error: function() {                    alert('There was some error performing the AJAX call!');                  }              });        }        function get_fev_icon(fav,eid)        {          var fav_id=fav.value;          $.ajax({                  type: "POST",                  url: "remote.php?action=get_fev_icon",                  data: {fav_id:fav_id},                  success : function(res){                      $('#icon_'+eid).html(res);                  },                  error: function() {                    alert('There was some error performing the AJAX call!');                  }              });        }         function open_food_popup(eid)        {             var meal_id=$('#meal_id_'+eid).val();            if(meal_id=='')            {               alert('First select food items');               return false;            }            var meal=$('#meals_'+eid).val();            if(meal=='')            {              meal='Food Composition';            }           $.ajax({                  type: "POST",                  url: "remote.php?action=get_food_compositionInfo",                  //dataType: "json",                  data: {meal_id:meal_id},                  success : function(result){                                          BootstrapDialog.show({                              title: meal,                              message:result                      });                                       },                  error: function() {                    alert('There was some error performing the AJAX call!');                  }              });                    }    </script>